function UpdateCategoryList20610() {

	jQuery('#category_subs20610').html("");
	jQuery('#category_subs_sub20610').html("");

	var catids = "";

	jQuery("#parent_category_list20610 > option:selected").each(function() {
		catids = catids + this.value+',';
	});

	showsubcategories20610(catids, '#subcategory_list20610', '#category_subs20610', 0);

	// sub cats
	 setTimeout( function(){
		UpdateCategoryListSub20610();
	 }  , 1000 );

}
function showsubcategories20610(catids, sel1, sel2, childof){

	// NOW TRY SHOW SUB CATEGORIES
	if(catids == ""){
	return;
	}

	// CHILD OF FOR SUB CATS
	if(childof == 0){
	cc = jQuery("#callcatidsbox").val();
	}else{
	cc  = childof;
	}

	jQuery.ajax({
		type: "POST",
		url: 'https://es10.premiummod.com/',
		dataType: 'json',
		data: {
				action: "load_taxonomy_list",
				taxonomy1: "country",
				parent:catids,
				child: cc,
				type:"list",
		},
		success: function(response) {

		 	if(response.total > 0){
				jQuery(sel1).show();
				jQuery(sel2).html(response.output);
			} else{
				jQuery(sel1).hide();
				jQuery(sel2).val("0");
			}

		},
		error: function(e) {
			console.log(e)
		}
	});

}
function UpdateCategoryListSub20610(){

	// SUB SUB CATS
	var catids = "";

	// GET FIRST VALUE IF NON ARE SELECTED
	if(jQuery("#category_subs20610 > option:selected").length == 0){

	 setTimeout( function(){

		catids = jQuery("#category_subs20610 option:first").val()+',';

		showsubcategories20610(catids, '#sub_subcategory_list20610', '#category_subs_sub20610', catids);

	  }  , 1000 );


	}else{

		jQuery("#category_subs20610 > option:selected").each(function() {
			catids = catids + this.value+',';
		});

		showsubcategories20610(catids, '#sub_subcategory_list20610', '#category_subs_sub20610', catids);

	}

}
