	jQuery('.customid-' + catid+' .required-field-xxx').each(function(i, obj) {
		jQuery(this).removeClass("required-field-xxx").addClass("required-field");
	});

	jQuery('.customid-0 .required-field-xxx').each(function(i, obj) {
		jQuery(this).removeClass("required-field-xxx").addClass("required-field");
	});
}

